// Get job title and candidates from webhook body
const jobTitle = $input.first().json.body.jobTitle || "Unknown Job";
const candidates = $input.first().json.body.candidates || [];

// Map candidates, attaching jobTitle to each one
return candidates.map(candidate => {
  return {
    json: {
      rank: candidate.rank,
      name: candidate.name,
      email: candidate.email,
      score: candidate.score,
      reason: candidate.reason,
      jobTitle: jobTitle, // ✅ attach job title here
    }
  };
});
